import React, {Component} from 'react';
//import logo from './logo.svg';
import './App.css';

// import TiltBox from './tiltbox';
// //import Tilt from 'react-vanilla-tilt';
// import InputField from './inputField';
// import ImageBox from './imagebox';
import ImageReader from './imageReader';
import SignIn from './signin';
import Register from './register';



class App extends Component {
  constructor (props){
    super(props);
    this.state = {
      name:'',
      register:false,
      email:'',
      password:'',
      passwordCheck:'',
      loggedin:false,
      }
    }

    getName = (e) => {
      this.setState({name:e.target.value})
    }

    getEmail = (e) => {
      this.setState({email:e.target.value});
      console.log("email input received")
    }

    getPassword = (e) => {
      this.setState({password:e.target.value})
    }

    getPasswordCheck = (e) => {
      this.setState({passwordCheck:e.target.value})
    }

    // submitDetails = (e) => {
    //  // alert("click!!");
    //   if (this.state.password === this.state.passwordCheck){
    //     //register user
    //     console.log(this.state);
    //  //  console.log('registered')
    //   } else {
    //     return (<div>Passwords don't match.</div>)
    //   }
    // }

    register = (e) => {
      e.preventDefault();
      this.setState({register:true});
    }

    submitRegistration = (e) => {
      e.preventDefault();
      if (this.state.password === this.state.passwordCheck){
        //register user
        console.log(this.state);
        fetch('http://localhost:3001/register', {
          method: 'POST',
          headers: {'Content-Type':'application/json'},
          body: JSON.stringify(this.state)
        }).then(res=>res.json())
        .then(response => console.log(response))
        .then(this.setState({loggedin:true}))
        .then(error => console.log(error));
     //  console.log('registered')
      } else {
        console.log("error")
      }
  //    alert("Registered!")
    }

    logout = (e) => {
      e.preventDefault();
      this.setState({loggedin:false});
    }

  render(){
    if(this.state.loggedin === false){
      if(this.state.register === false){
        return(
          <div className = "App">
            <div className='signInBox'>
              <SignIn className='signIn'
              email = {this.state.email}
              password = {this.state.password}
              submitEmail = {this.getEmail}
              submitPassword = {this.getPassword}
              click = {this.submitDetails}
              register = {this.register} />
            </div>
          </div>)
      } else {
        return (<div className = "App">
                  <div className='signInBox'>
                    <Register className='signIn'
                      name = {this.state.name}
                      email = {this.state.email}
                      password = {this.state.password}
                      passwordCheck = {this.state.passwordCheck}
                      submitName = {this.getName}
                      submitEmail = {this.getEmail}
                      submitPassword = {this.getPassword}
                      submitPasswordCheck = {this.getPasswordCheck}
                      click = {this.submitRegistration}
                       />
                </div>
              </div>)}
      } else { 
      return (
        <div className='CoreApp'>
          <ImageReader className = 'CoreApp' logout={this.logout}/>
        </div>
    );}
  }
}

export default App;
